latest-items.map:
  function (doc) {
  if(doc.type && doc.type === 'item' && doc.created_at){
        emit(doc.created_at, null);
    }
}

all-item.map:
function (doc) {
  if(doc.type && doc.type=="item"){
    var current_date = new Date();
    var date = new Date();
    date.setMonth(date.getMonth() - 2);
    var ad_date = new Date(doc.created_at);
    if(ad_date.getTime() >= date.getTime() && ad_date.getTime() <= current_date.getTime()){
      emit(doc._id, 1);
    }
  }
}
